correct.answer:
for i in 1...10 {
	if i == 8 {
		print("Found number 8!")
	}
}

var counter = 0
while counter < 5 {
	counter += 1
	print("Counting: \(counter)")
}

var people = 2
while people < 10 {
	people += 2
	if people == 10 {
		print("We got 10 people.")
	}
}

for i in 1...15 {
	if i % 3 == 0 {
		if i % 5 == 0 {
			print("\(i) is a multiple of both 3 and 5.")
		}
	}
}

