func checkFor(_ searchName: String, in names: [String]) -> Bool {
	for name in names {
		if name == searchName {
			return true
		}
	}
}

func paintWalls(tastefully: Bool, color: inout String) {
	if tastefully {
		color = "cream"
	} else {
		color = "tartan"
	}
}
let color = ""
paintWalls(tastefully: true, color: &color)

