correct.answer:
var bagels = 5
repeat {
	print("Someone ate a bagel!")
	bagels -= 1
} while bagels > 0

var encores = 0
repeat {
	print("Let's play that piece again.")
	encores += 1
} while encores < 3

var frogs = 4
repeat {
	for _ in 1...frogs {
		print("Ribbit!")
	}
} while false

var scales = ["A", "B", "C", "D", "E"]
var scaleCounter = 0
repeat {
	print("Play the \(scales[scaleCounter]) scale")
	scaleCounter += 1
} while scaleCounter < 3

var countdown: Int = 5
repeat {
	print("\(countdown)...")
	countdown -= 1
} while countdown > 0
print("Lift off!")

var number = 0
repeat {
	number += 2
	print("\(number) is an even number.")
} while number < 20

