}

var costToShootMovie = { (location: String) -> Int in
	if location == "UK" {
		return 1_000_000
	} else if location == "US" {
		return 5_000_000
	} else {
		return 500_000
	}
}

var difficultyRating = { (trick: String) -> Int in
	if trick == "ollie" {
		return 1
	} else if trick == "Yoyo Plant" {
		return 3
	} else if trick == "900" {
		return 5
	} else {
		return 0
	}
}
print(difficultyRating("ollie"))

