}
func chargePhone(atHome: Bool) throws {
	if atHome {
		print("Phone is charging...")
	} else {
		throw ChargeError.noPower
	}
}

enum WifiError: Error {
	case noNetwork
	case noSignal
}
func connectToWifi(_ password: String) throws {
	if password == "" {
		throw WifiError.badPassword
	}
	print("You're connected.")
}

